import React, { useState } from 'react';

const T12_74_UseStateBasics = () => {
  //let title = 'random text'
  const handleClick = () => {
    title = 'Hello World';
    console.log('title', title);
  };
  // fragment
  return (
    <>
      <h2>{title}</h2>
      <button type='button' className='btn' onClick={handleClick}>
        change title
      </button>
    </>
  );
};

export default UseStateBasics;
